package loop;

import java.util.Scanner;

public class ForLoopsHW {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner input = new Scanner(System.in);
//Sum of Odd
		int p= 1;
		int counter=0;
		for (p=1;p<=1; p=p+2) {
			counter= counter+p;
			System.out.println("Sum= "+counter);
		}
		
	}

}
